function Slidezy(selector,options={}){this.container=document.querySelector(selector);if(!this.container){console.error(`Slidezy: Container "${selector}" not found!`);return}this.opt=Object.assign({items:1,speed:300,loop:true,autoPlay:false},options);this.slides=Array.from(this.container.children);this._init();this._updatePosition()}Slidezy.prototype._init=function(){this.container.classList.add("slidezy-wrapper");this._createTrack();this._createNavigation()};Slidezy.prototype._createTrack=function(){this.track=document.createElement("div");this.track.className="slidezy-track";if(this.opt.loop){const cloneHead=this.slides.slice(-this.opt.items).map(clone=>clone.cloneNode(true));const cloneTail=this.slides.slice(0,this.opt.items).map(clone=>clone.cloneNode(true));this.slides=cloneHead.concat(this.slides.concat(cloneTail))}this.slides.forEach((slide,index)=>{slide.classList.add("slidezy-slide");slide.style.flexBasis=`${100/this.opt.items}%`;this.track.append(slide)});this.container.append(this.track)};Slidezy.prototype._createNavigation=function(){this.nextBtn=document.createElement("button");this.preBtn=document.createElement("button");this.preBtn.className="slidezy-pre";this.nextBtn.className="slidezy-next";this.nextBtn.innerHTML="&#10095";this.preBtn.innerHTML="&#10094";this.container.append(this.nextBtn,this.preBtn);this.currentStep=this.opt.loop?this.opt.items:0;this.preBtn.onclick=()=>this.moveSlide(-1);this.nextBtn.onclick=()=>this.moveSlide(1)};Slidezy.prototype.moveSlide=function(step){if(this._isTransitioning)return;this._isTransitioning=true;this.maxIndex=this.slides.length-this.opt.items;this.currentStep=Math.min(Math.max(this.currentStep+step,0),this.maxIndex);setTimeout(()=>this._infiniteLoop(),this.opt.speed);this._updatePosition()};Slidezy.prototype._infiniteLoop=function(){if(this.opt.loop){if(this.currentStep>=this.maxIndex){this.currentStep=this.opt.items}if(this.currentStep<=0){this.currentStep=this.maxIndex-this.opt.items}this._updatePosition(true)}this._isTransitioning=false};Slidezy.prototype._updatePosition=function(instant=false){this.track.style.transition=instant?"none":`transform ${this.opt.speed}ms ease`;this.track.style.transform=`translateX(${-this.currentStep*100/this.opt.items}%)`};Slidezy.prototype.autoPlay=function(){setInterval(()=>{this.moveSlide(1)},2e3)};