function Slidezy(selector,options={}){this.container=document.querySelector(selector);if(!this.container){console.error(`Slidezy: Container "${selector}" not found!`);return}this.opt=Object.assign({items:1,loop:true},options);this.slides=Array.from(this.container.children);this._init()}Slidezy.prototype._init=function(){this.container.classList.add("slidezy-wrapper");this._createTrack();this._createNavigation()};Slidezy.prototype._createTrack=function(){this.track=document.createElement("div");this.track.className="slidezy-track";this.slides.forEach(slide=>{slide.classList.add("slidezy-slide");slide.style.flexBasis=`${100/this.opt.items}%`;this.track.append(slide)});this.container.append(this.track)};Slidezy.prototype._createNavigation=function(){this.nextBtn=document.createElement("button");this.preBtn=document.createElement("button");this.preBtn.className="slidezy-pre";this.nextBtn.className="slidezy-next";this.nextBtn.innerHTML="&#10095";this.preBtn.innerHTML="&#10094";this.container.append(this.nextBtn,this.preBtn);this.currentStep=0;this.preBtn.onclick=()=>this.moveSlide(-1);this.nextBtn.onclick=()=>this.moveSlide(1)};Slidezy.prototype.moveSlide=function(step){if(this.opt.loop){this.currentStep=(this.currentStep+step+this.slides.length)%this.slides.length}else{this.currentStep=Math.min(Math.max(this.currentStep+step,0),this.slides.length-this.opt.items)}this.track.style.transform=`translateX(${-this.currentStep*100/this.opt.items}%)`};